mapper.select:
SELECT UUID() AS UUID ,#{tabsUuid} AS TABS_UUID ,REMARKS AS COLS_DESC ,COLUMN_NAME AS COLS_NAME ,CHARACTER_MAXIMUM_LENGTH AS COLS_LENGTH ,COLUMN_TYPE AS COLS_TYPE ,NULLABLE AS NULLABLE ,ORDINAL_POSITION AS SORT FROM INFORMATION_SCHEMA.COLUMNS C
and TABLE_NAME = #{tabsName}
and COLUMN_NAME NOT IN
#{idItem}
