mapper.select:
SELECT UUID() AS uuid ,#{jdbcUuid} as jdbc_uuid, concat('`',TABLE_SCHEMA,'`.`',TABLE_NAME,'`') AS TABS_NAME ,TABLE_ROWS as tabsRows ,TABLE_COMMENT as tabsDesc FROM `INFORMATION_SCHEMA`.`TABLES` a WHERE TABLE_SCHEMA not in ( 'information_schema' ,'mysql','performance_schema','sys') AND TABLE_TYPE = 'BASE TABLE'
and concat('`',TABLE_SCHEMA,'`.`',TABLE_NAME,'`') like CONCAT('%',#{tname},'%')
and concat('`',TABLE_SCHEMA,'`.`',TABLE_NAME,'`') NOT IN
#{idItem}
limit ${key_offset},${pageSize}
